import styled from 'styled-components'

export const AboutMeContainer = styled.div`
    background: #de5542;
`

export const AboutMeTop = styled.div`
    display: flex;
    flex-direction: row;
`

export const AboutMeImgContainer = styled.div`
    align-items: center;
    padding: 5rem;
`
export const AboutMeImgBorder = styled.div`
    height: 200px;
    width: 200px;
    border-radius: 50%;
    padding: 2rem;
    border: 5px solid #fff;
    align-items: center;
`

export const AboutMeImage = styled.img`
    height: 200px;
    width: 200px;
    border-radius: 50%;
    align-self: center;
`

export const AboutMeIntroContainer = styled.div`
    display: flex;
    flex-direction: column;
`
export const AboutMeIntro = styled.div`
    color: #fff;
    font-size: 10%;
`
export const AboutMeOccupation = styled.div`
    color: #fff;
    font-size: 10%;
`
