import styled from 'styled-components';

export const ResumeContainer = styled.div`
    background-color: white;
    padding: 40px 30px;
    display: flex;
    flex-direction: column;
`

export const ResumeTitle = styled.h1`
    color: #d59c6e;
    font-weight: 700;
    margin-left: 30px;
`
export const ResumeBodyContainer = styled.div`
    display: flex;
    /* height: 100vh; */
`
export const ResumeImageContainer = styled.div`
    display: flex;
    /* background: black; */
    padding: 5px 20px;
    margin-left: 30px;
    justify-content: center;
    flex: 0.4;
`
export const ResumeImage = styled.img`
    height: 400px;
    width: 300px;
    border-radius: 10px;
    /* align-self: flex-start; */
`

export const ResumeDetailsContainer = styled.div`
    /* background: red; */
    flex: 0.6;
    height: 10vh;
    width: 100%;
`
