import styled from 'styled-components'

export const WorkContainer = styled.div`
    font-family: 'Montserrat', sans-serif;
    background: #3F5371;
    height: 100vh;
    display: flex;
    flex-direction: column;
    color: #F2E5D9;
    justify-content: center;
`

export const WorkInfoContainer = styled.div`
    display: flex;
    flex-direction: column;
    font-size: 50px;
    justify-content: space-evenly;
`

export const WorkExperienceTitle = styled.div`
    background: #F2E5D9;
    color: #3F5371;
    margin-left: 60px;
`
export const WorkExperienceContainer = styled.div`
    display: flex;
`

export const WorkImageContainer = styled.div`
    align-items: center;
`
export const WorkImageDiv = styled.div`
    border-radius: 50%;
    border: 3px solid #3F5371;
    padding: 10px;
    align-items: center;
    justify-content: center;
`
export const WorkImage = styled.div``

export const WorkDetailsContainer = styled.div``
