import styled from 'styled-components'

export const AboutMeContainer = styled.div`
    background: #de5542;
    height: 100%;
`

export const AboutMeTop = styled.div`
    display: flex;
    flex-direction: column;
`

export const AboutMeTopFirst = styled.div`
    align-items: center;
    display: flex;
    justify-content: center;
    flex-direction: row;
`

export const AboutMeImgContainer = styled.div`
    align-items: center;
    padding: 5rem;
`
export const AboutMeImgBorder = styled.div`
    height: 310px;
    width: 300px;
    border-radius: 50%;
    padding: 20px;
    border: 3px solid #fff;
    align-items: center;
`

export const AboutMeImage = styled.img`
    height: 260px;
    width: 250px;
    border-radius: 50%;
    /* align-self: center; */
`

export const AboutMeIntroContainer = styled.div`
    display: flex;
    flex-direction: column;
    color: #fff;
    font-size: 220%;
`
export const AboutMeIntro = styled.div``
