import styled from 'styled-components'

export const WorkContainer = styled.div`
    font-family: 'Montserrat', sans-serif;
    background: #3F5371;
    height: 100vh;
    display: flex;
    flex-direction: column;
    color: #F2E5D9;
    justify-content: center;
`

export const WorkInfoContainer = styled.div`
    display: flex;
    flex-direction: column;
    font-size: 40px;
    justify-content: space-evenly;
    margin-left: 60px;
`

export const WorkExperienceContainer = styled.div`
    display: flex;
    flex-direction: column;
`

export const WorkExperienceTitle = styled.div`
    background: #F2E5D9;
    color: #3F5371;
    padding: 0px 40px;
    border-radius: 10px;
    margin-right: -10px;
`
export const WorkExperienceImageDetailsContainer = styled.div`
    display: flex;
    align-items: center;
    gap: 30px;
    /* justify-content: center; */
`

export const WorkImageContainer = styled.div`
    align-items: center;
`
export const WorkImageDiv = styled.div`
    /* border-radius: 50%;
    border: 3px solid #3F5371;
    padding: 10px;
    align-items: center;
    justify-content: center; */
`
export const WorkImage = styled.div``

export const WorkDetailsContainer = styled.div`
    /* align-items: center; */
    justify-content: center;
`
